// ==== MATH FUNCTIONS ====

function Fib(a) {
 var b = [0, 1, 2], c = Math.floor(a);
 for (var i = b.length; i <= a; i++) {
  //Next fibonacci number = previous + one before previous
  b[i] = b[i - 2] + b[i - 1];
 };
 a = Math.pow(a - c, 1.15);
 return b[c] + (b[Math.ceil(a)] - b[c]) * a;
}

function fib_Spiral(){
 var Fibonacci = [];
 function setup(){
  createCanvas(windowWidth, windowHeight);
  background(255);
  frameRate(10);
 }

 function draw(){
  background(255);
  stroke(0);
  noFill();
  strokeWeight(5);
  translate(windowWidth/2, windowHeight/2);
  rotate(-PI / 6 * frameCount);
  for( var i = 0; i < 20; i ++){
    var a = i <= 1 ? 1 : Fibonacci[i-1] + Fibonacci[i-2];
    Fibonacci.push(a);
    arc(0,0,a * 2,a * 2,0,PI / 2);
    rotate(PI / 2);
    translate(-Fibonacci[i-1],0); 
  } 
 }
}
